mapper.select:
select
  id, user_id, column_id, create_time, update_time
from bs_column_subscribe


        and id = #{id}


        and user_id = #{userId}


        and column_id = #{columnId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from bs_column_subscribe


        and id = #{id}


        and user_id = #{userId}


        and column_id = #{columnId}


        and create_time = #{createTime}


        and update_time = #{updateTime}
